package Yogesh;
import java.util.*;
public class add2numberwithoutoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan=new Scanner(System.in);
		int a=scan.nextInt();
		int b=scan.nextInt();
		for(int i=0;i<b;i++) {
			a++;
		}
		System.out.println("Sum of two number is : "+a);

	}

}
